import React, { useContext, useState } from 'react';
import { TodoContext } from '../context/TodoContext';

const Todo = ({ id, title, completed, showToastMessage }) => {
    const [todos, setTodos] = useContext(TodoContext);
    const [editing, setEditing] = useState(false); // Define editing state
    const [editTitle, setEditTitle] = useState(title); // Define editTitle state
    const [showEditToast, setShowEditToast] = useState(false); // State for edit toast
    const [showDeleteToast, setShowDeleteToast] = useState(false);

    const completeTodo = (e) => {
        const updatedTodos = todos.map((item) => {
            if (item.id === id) {
                item.completed = e.target.checked;
            }
            return item;
        });
        setTodos(updatedTodos);
    };

    const deleteTodo = (e) => {
        e.preventDefault();
        const updatedTodos = todos.filter((item) => item.id !== id);
        setTodos(updatedTodos);
        showToastMessage('Deleted successfully!');
    };

    const handleEdit = () => {
        setEditing(true); // Set editing state to true when edit icon is clicked
    };

    const handleEditChange = (e) => {
        setEditTitle(e.target.value);
    };

    const handleEditSubmit = (e) => {
        e.preventDefault();
        const updatedTodos = todos.map((item) => {
            if (item.id === id) {
                item.title = editTitle;
            }
            return item;
        });
        setTodos(updatedTodos);
        setEditing(false); // Set editing state to false after editing is submitted
        showToastMessage('Edited successfully!'); // Show toast message after editing
    };
    
    const editTodo = (e) => {
        e.preventDefault();
        handleEdit(); // Start editing mode
        showToastMessage('Editing mode activated!');
    };
    

    return (
            <li>
                <div className="todoapp_todo">
                    <div className="todoapp_todos_left">
                        <input
                            type="checkbox"
                            id={id}
                            className="todoapp_todos_left-checkbox"
                            checked={completed}
                            onChange={completeTodo}
                        />
                        {editing ? (
                            <input
                                type="text"
                                value={editTitle}
                                onChange={handleEditChange}
                                onBlur={handleEditSubmit}
                            />
                        ) : (
                            <div className="todoapp_todos_left-text">{title}</div>
                        )}
                    </div>
                    <div className="todoapp_todos_right">
                        <i className="fas fa-edit todoapp_todos_right-iconedit" onClick={editTodo}></i> {/* Add onClick event for editing */}
                        <i className="fas fa-times todoapp_todos_right-icondelete" onClick={deleteTodo}></i> {/* Fix class attribute */}
                    </div>
                </div>
                {/* Edit toast */}
                {showEditToast && (
                    <div className="container">
                        <div className="message">
                            <i className="fas fa-check-circle message_icon"></i>
                            <div className="message_text">Editing mode activated!</div>
                        </div>
                        <div className="coutdown"></div>
                    </div>
                )}
                {/* Delete toast */}
                {showDeleteToast && (
                    <div className="container">
                        <div className="message">
                            <i className="fas fa-check-circle message_icon"></i>
                            <div className="message_text">Deleted successfully!</div>
                        </div>
                        <div className="coutdown"></div>
                    </div>
                )}
            </li>
    );
};

export default Todo;
