import { useContext } from "react";
import { TodoContext } from "../context/TodoContext";

const Todo = ({ id, title, completed, showToastMessage }) => {
    const [todos, setTodos] = useContext(TodoContext);

    const completeTodo = (e) => {
        const updatedTodos = todos.map((item) => {
            if (item.id === id) {
                item.completed = e.target.checked;
            }
            return item;
        });
        setTodos(updatedTodos);
    };

    const deleteTodo = (e) => {
        e.preventDefault();
        const updatedTodos = todos.filter((item) => item.id !== id);
        setTodos(updatedTodos);
        showToastMessage('Deleted successfully!');
    };

    const editTodo = (e) => {
        e.preventDefault();

    }

    return (
            <li>
                <div className="todoapp_todo">
                    <div className="todoapp_todos_left">
                        <input
                            type="checkbox"
                            id={id}
                            className="todoapp_todos_left-checkbox"
                            checked={completed}
                            onChange={completeTodo}
                        />
                        {editing ? (
                        <input
                            type="text"
                            value={editTitle}
                            onChange={handleEditChange}
                            onBlur={handleEditSubmit}
                        />
                    ) : (
                        <div className="todoapp_todos_left-text">{title}</div>
                    )}
                    </div>
                    <div className="todoapp_todos_right">
                        <i className="fas fa-edit todoapp_todos_right-iconedit"></i>
                        <i class="fas fa-times todoapp_todos_right-icondelete" onClick={deleteTodo}></i>
                    </div>
                </div>
            </li>
    );
};

export default Todo;
