import React from 'react';

const Toast = ({ message, show }) => {
    return (
        show && (
            <div className="toast">
                <i className="fas fa-info-circle toast-icon"></i> {/* Use the Font Awesome icon */}
                {message}
            </div>
        )
    );
}

export default Toast;
