import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'; // Import FontAwesomeIcon from the FontAwesome library
import { faInfoCircle } from '@fortawesome/free-solid-svg-icons'; // Import the icon you want to use

const Toast = ({ message, show }) => {
    return (
        show && (
            <div className="toast">
                <FontAwesomeIcon icon={faInfoCircle} className="toast-icon" /> {/* Display the icon */}
                {message}
            </div>
        )
    );
}

export default Toast;
