import { useContext, useEffect, useState } from "react";
import { v4 as uuidv4 } from 'uuid';
import { TodoContext } from "../context/TodoContext";

const AddTodo = () => {

    const [ title, setTitle ] = useState('');
    const [ todos, setTodos ] = useContext( TodoContext );

    const addTodo = (e) => {
        e.preventDefault();
        console.log('title: ', title);
        if ( '' === title || undefined === title ) {
            alert( 'Field can not be blank' );
            return;
        }
        const newTodos = [ ...todos, { id: uuidv4(), title: title, completed: false } ];
        setTodos( newTodos );
        setTitle( '' );
    }

    useEffect( () => {
        localStorage.setItem( 'todos', JSON.stringify( todos ) );
    }, [todos] );

    return (
        <>
            <div class="todoapp_input">
                <div class="todoapp_input_text">
                <input value={ title } className="form-input" onChange={ e => setTitle( e.target.value ) } placeholder="Add todo.." />
                </div>
                <button class="todoapp_input_btn button_add" onClick={ addTodo }>+</button>
                <button class="todoapp_input_btn button_edit">Edit</button>
            </div>

        </>
    );
}

export default AddTodo;
